-- Name: fn_actualizar_estado_neumatico(); Type: FUNCTION; Schema: public; Owner: test_user
--

CREATE FUNCTION public.fn_actualizar_estado_neumatico() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_kilometros_recorridos integer := 0;
    v_odometro_anterior integer;
    v_neumatico_record record;
    v_modelo_record record;
BEGIN
    RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Iniciando para evento tipo: %, neumatico ID: %', NEW.tipo_evento, NEW.neumatico_id;

    -- Obtener estado actual del neumático
    SELECT * INTO v_neumatico_record FROM public.neumaticos WHERE id = NEW.neumatico_id FOR UPDATE;
    IF NOT FOUND THEN RAISE WARNING '[...] Neumático ID % no encontrado.', NEW.neumatico_id; RETURN NEW; END IF;

    -- Obtener max_reencauches (puede ser necesario para otros casos)
    SELECT reencauches_maximos INTO v_modelo_record FROM public.modelos_neumatico WHERE id = v_neumatico_record.modelo_id;
    IF NOT FOUND THEN RAISE WARNING '[...] Modelo ID % no encontrado.', v_neumatico_record.modelo_id; v_modelo_record.reencauches_maximos := 99; END IF;

    -- Cálculo de Kilometraje (sin cambios)
    IF v_neumatico_record.estado_actual = 'INSTALADO' AND NEW.tipo_evento IN ('DESMONTAJE', 'INSPECCION', 'DESECHO') AND NEW.odometro_vehiculo_en_evento IS NOT NULL THEN
        SELECT odometro_vehiculo_en_evento INTO v_odometro_anterior
        FROM public.eventos_neumaticos
        WHERE neumatico_id = NEW.neumatico_id
          AND tipo_evento IN ('INSTALACION', 'INSPECCION')
          AND odometro_vehiculo_en_evento IS NOT NULL
          AND timestamp_evento < NEW.timestamp_evento
        ORDER BY timestamp_evento DESC
        LIMIT 1;
        IF v_odometro_anterior IS NOT NULL AND NEW.odometro_vehiculo_en_evento > v_odometro_anterior THEN
            v_kilometros_recorridos := NEW.odometro_vehiculo_en_evento - v_odometro_anterior;
        ELSE v_kilometros_recorridos := 0; END IF;
        RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] KM Recorridos calculados: %', v_kilometros_recorridos;
    ELSE v_kilometros_recorridos := 0; END IF;

    -- Actualización del Neumático
    CASE NEW.tipo_evento

        -- *** CASO INSTALACION: MANEJADO POR TRIGGER ***
        WHEN 'INSTALACION' THEN
            RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Procesando INSTALACION (Manejando estado/ubicación).';
            UPDATE public.neumaticos SET
                estado_actual = 'INSTALADO',
                ubicacion_actual_vehiculo_id = NEW.vehiculo_id,
                ubicacion_actual_posicion_id = NEW.posicion_id,
                ubicacion_almacen_id = NULL, -- <-- Trigger asegura NULL
                fecha_ultimo_evento = NEW.timestamp_evento,
                kilometraje_acumulado = 0, -- Resetear KM aquí
                actualizado_en = now(),
                actualizado_por = NEW.usuario_id
            WHERE id = NEW.neumatico_id;

        -- *** CASO DESMONTAJE: MANEJADO POR TRIGGER *** (Basado en tu backup original)
        WHEN 'DESMONTAJE' THEN
            RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Procesando DESMONTAJE.';
             UPDATE public.neumaticos SET
                estado_actual = NEW.destino_desmontaje,
                ubicacion_actual_vehiculo_id = NULL,
                ubicacion_actual_posicion_id = NULL,
                ubicacion_almacen_id = CASE WHEN NEW.destino_desmontaje <> 'DESECHADO' THEN NEW.almacen_destino_id ELSE NULL END, -- Asigna almacén si no es desecho
                fecha_ultimo_evento = NEW.timestamp_evento,
                kilometraje_acumulado = v_neumatico_record.kilometraje_acumulado + v_kilometros_recorridos,
                fecha_desecho = CASE WHEN NEW.destino_desmontaje = 'DESECHADO' THEN NEW.timestamp_evento::date ELSE fecha_desecho END,
                motivo_desecho_id = CASE WHEN NEW.destino_desmontaje = 'DESECHADO' THEN NEW.motivo_desecho_id_evento ELSE motivo_desecho_id END,
                actualizado_en = now(),
                actualizado_por = NEW.usuario_id
             WHERE id = NEW.neumatico_id;

        -- *** CASO DESECHO: MANEJADO POR TRIGGER *** (Basado en tu backup original)
        WHEN 'DESECHO' THEN
             RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Procesando DESECHO.';
             IF v_neumatico_record.estado_actual != 'DESECHADO' THEN -- Evitar doble desecho
                 UPDATE public.neumaticos SET
                     estado_actual = 'DESECHADO',
                     fecha_desecho = NEW.timestamp_evento::date,
                     motivo_desecho_id = NEW.motivo_desecho_id_evento,
                     fecha_ultimo_evento = NEW.timestamp_evento,
                     kilometraje_acumulado = v_neumatico_record.kilometraje_acumulado + v_kilometros_recorridos,
                     ubicacion_actual_vehiculo_id = NULL,
                     ubicacion_actual_posicion_id = NULL,
                     ubicacion_almacen_id = NULL, -- Asegurar NULL
                     actualizado_en = now(),
                     actualizado_por = NEW.usuario_id
                 WHERE id = NEW.neumatico_id;
             END IF;

         -- *** CASO INSPECCION: MANEJADO POR TRIGGER *** (Basado en tu backup original)
         WHEN 'INSPECCION' THEN
             RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Procesando INSPECCION.';
             IF v_kilometros_recorridos > 0 THEN
                 UPDATE public.neumaticos SET kilometraje_acumulado = v_neumatico_record.kilometraje_acumulado + v_kilometros_recorridos, fecha_ultimo_evento = NEW.timestamp_evento, actualizado_en = now(), actualizado_por = NEW.usuario_id WHERE id = NEW.neumatico_id;
             ELSE -- Solo actualizar fecha si no hay KM
                 UPDATE public.neumaticos SET fecha_ultimo_evento = NEW.timestamp_evento, actualizado_en = now(), actualizado_por = NEW.usuario_id WHERE id = NEW.neumatico_id;
             END IF;

         -- *** CASOS REPARACION/REENCAUCHE ENTRADA: MANEJADO POR TRIGGER ***
         WHEN 'REPARACION_ENTRADA' THEN
             RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Procesando REPARACION_ENTRADA.';
             UPDATE public.neumaticos SET estado_actual = 'EN_REPARACION', fecha_ultimo_evento = NEW.timestamp_evento, actualizado_en = now(), actualizado_por = NEW.usuario_id, ubicacion_almacen_id = NEW.almacen_destino_id, ubicacion_actual_vehiculo_id = NULL, ubicacion_actual_posicion_id = NULL WHERE id = NEW.neumatico_id;

         WHEN 'REENCAUCHE_ENTRADA' THEN
             RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Procesando REENCAUCHE_ENTRADA.';
             IF v_neumatico_record.reencauches_realizados >= v_modelo_record.reencauches_maximos THEN RAISE WARNING '[...] Neumático ID % enviado a reencauche superando límite %.', NEW.neumatico_id, v_modelo_record.reencauches_maximos; END IF;
             UPDATE public.neumaticos SET estado_actual = 'EN_REENCAUCHE', fecha_ultimo_evento = NEW.timestamp_evento, actualizado_en = now(), actualizado_por = NEW.usuario_id, ubicacion_almacen_id = NEW.almacen_destino_id, ubicacion_actual_vehiculo_id = NULL, ubicacion_actual_posicion_id = NULL WHERE id = NEW.neumatico_id;

         -- *** CASOS REPARACION/REENCAUCHE SALIDA: Lógica en API, Trigger solo actualiza audit ***
         WHEN 'REPARACION_SALIDA' THEN
             RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Evento REPARACION_SALIDA recibido. Lógica de estado/ubicación manejada por API.';
             UPDATE public.neumaticos SET fecha_ultimo_evento = NEW.timestamp_evento, actualizado_en = now(), actualizado_por = NEW.usuario_id WHERE id = NEW.neumatico_id;

         WHEN 'REENCAUCHE_SALIDA' THEN
             RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Evento REENCAUCHE_SALIDA recibido. Lógica de estado/contadores/ubicación manejada por API.';
             UPDATE public.neumaticos SET fecha_ultimo_evento = NEW.timestamp_evento, actualizado_en = now(), actualizado_por = NEW.usuario_id WHERE id = NEW.neumatico_id;

        -- Otros casos (ROTACION, TRANSFERENCIA, AJUSTE_INVENTARIO): API maneja estado/ubicación, trigger solo audit.
        ELSE
            RAISE NOTICE '[TRIGGER FN_ACT_ESTADO] Evento tipo % no requiere acción específica del trigger (manejado por API o solo audit).', NEW.tipo_evento;
            UPDATE public.neumaticos SET fecha_ultimo_evento = NEW.timestamp_evento, actualizado_en = now(), actualizado_por = NEW.usuario_id WHERE id = NEW.neumatico_id;

    END CASE;

    RETURN NEW;
END;
$$;


ALTER FUNCTION public.fn_actualizar_estado_neumatico() OWNER TO test_user;

