-- Name: fn_check_ubicacion_constraint(); Type: FUNCTION; Schema: public; Owner: test_user
--

CREATE FUNCTION public.fn_check_ubicacion_constraint() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    IF NOT (
        (((NEW.ubicacion_almacen_id IS NOT NULL) AND (NEW.ubicacion_actual_vehiculo_id IS NULL) AND (NEW.ubicacion_actual_posicion_id IS NULL) AND (NEW.estado_actual <> 'INSTALADO'::public.estado_neumatico_enum)) OR
         ((NEW.ubicacion_almacen_id IS NULL) AND (NEW.ubicacion_actual_vehiculo_id IS NOT NULL) AND (NEW.ubicacion_actual_posicion_id IS NOT NULL) AND (NEW.estado_actual = 'INSTALADO'::public.estado_neumatico_enum)) OR
         ((NEW.ubicacion_almacen_id IS NULL) AND (NEW.ubicacion_actual_vehiculo_id IS NULL) AND (NEW.ubicacion_actual_posicion_id IS NULL) AND (NEW.estado_actual <> 'INSTALADO'::public.estado_neumatico_enum)))
    ) THEN
        RAISE EXCEPTION 'VIOLATION: Ubicacion mutuamente exclusiva invalida - Estado: %, Alm: %, Veh: %, Pos: %',
            NEW.estado_actual, NEW.ubicacion_almacen_id, NEW.ubicacion_actual_vehiculo_id, NEW.ubicacion_actual_posicion_id
            USING ERRCODE = 'check_violation', CONSTRAINT = 'trg_check_ubicacion_constraint';
    END IF;
    RETURN NEW;
END;
$$;


