--
-- Name: vw_neumaticos_instalados_optimizada; Type: VIEW; Schema: public; Owner: test_user
--

CREATE VIEW public.vw_neumaticos_instalados_optimizada AS
 SELECT neumatico.id,
    neumatico.id AS neumatico_id,
    neumatico.numero_serie,
    neumatico.dot,
    modelo.nombre_modelo,
    modelo.medida,
    fabricante.nombre AS fabricante,
    vehiculo.placa,
    vehiculo.numero_economico,
    tipo_vehiculo.nombre AS tipo_vehiculo,
    posicion.codigo_posicion,
    insp.profundidad_remanente_mm AS profundidad_actual_mm,
    insp.presion_psi AS presion_actual_psi,
    neumatico.kilometraje_acumulado AS kilometraje_neumatico_acumulado,
    neumatico.vida_actual,
    neumatico.reencauches_realizados
   FROM ((((((public.neumaticos neumatico
     LEFT JOIN public.modelos_neumatico modelo ON ((neumatico.modelo_id = modelo.id)))
     LEFT JOIN public.fabricantes_neumatico fabricante ON ((modelo.fabricante_id = fabricante.id)))
     LEFT JOIN public.vehiculos vehiculo ON ((neumatico.ubicacion_actual_vehiculo_id = vehiculo.id)))
     LEFT JOIN public.tipos_vehiculo tipo_vehiculo ON ((vehiculo.tipo_vehiculo_id = tipo_vehiculo.id)))
     LEFT JOIN public.posiciones_neumatico posicion ON ((neumatico.ubicacion_actual_posicion_id = posicion.id)))
     LEFT JOIN LATERAL ( SELECT ei.profundidad_remanente_mm,
            ei.presion_psi
           FROM public.eventos_neumaticos ei
          WHERE ((ei.neumatico_id = neumatico.id) AND (ei.tipo_evento = 'INSPECCION'::public.tipo_evento_neumatico_enum) AND (ei.profundidad_remanente_mm IS NOT NULL))
          ORDER BY ei.timestamp_evento DESC
         LIMIT 1) insp ON (true))
  WHERE (neumatico.estado_actual = 'INSTALADO'::public.estado_neumatico_enum);


